import React, { useContext, useEffect, useState } from 'react';
import './CSS/MarketplaceCategory.css';
import { ShopContext } from '../Context-api/ShopContext';
import { Item } from '../Components/Item/Item';
import Swiper from 'swiper';
import 'swiper/css/bundle';

export const MarketPlaceCategory = (props) => {
  const { all_product } = useContext(ShopContext);
  const [specificProducts, setSpecificProducts] = useState([]);

  useEffect(() => {
    console.log('Fetching specific products');
    // Specify the product IDs you want to fetch
    const productIds = [44, 50, 43, 3, 40];

    // Filter the all_product array to get specific products
    const products = all_product.filter(product => productIds.includes(product.id));
    setSpecificProducts(products);

    // Initialize Swiper after component mounts
    const swiper = new Swiper('.tranding-slider', {
      loop: true,
      slidesPerView: 4,
      spaceBetween: 70,
      navigation: {
        nextEl: '.swiper-button-next',
        prevEl: '.swiper-button-prev',
      },
      pagination: {
        el: '.swiper-pagination',
        clickable: true,
      },
    });

    // Destroy Swiper instance when component unmounts
    return () => {
      swiper.destroy();
    };
  }, [all_product]); // Re-run effect when all_product changes

  return (
    <div className='marketplace-category'>
      <div className='MarketPlaceCategory-indexsort'>
        <p>
          <span>Showing 1-12</span> out of {specificProducts.length} products
        </p>
      </div>
      <section id='tranding'>
        <div className="swiper tranding-slider">
          <div className="swiper-wrapper">
            {/* Render specific products fetched from the backend */}
            {specificProducts.map(product => (
              <div className="swiper-slide tranding-slide" key={product.id}>
                <Item
                  id={product.id}
                  name={product.name}
                  image={product.image}
                  new_price={product.new_price}
                  old_price={product.old_price}
                />
              </div>
            ))}
          </div>
          <div className="swiper-pagination"></div>
          <div className="swiper-button-prev"></div>
          <div className="swiper-button-next"></div>
        </div>
      </section>
    </div>
  );
};
